import React, { useState } from 'react'
import {VStack,  Input, FormControl, FormLabel, InputGroup, InputRightElement } from "@chakra-ui/react";
// import { } from '@chakra-ui/form-control';

const signup = () => {
const [show, setShow]= useState(false);
const [name, setName]= useState();
const [email, setEmail]= useState();
const [password, setPassword]= useState();
const [confirmpassword, setConfirmpassword]= useState();
const [pic, setPic]= useState();


  return (
    <VStack spacing ="5px">
        <FormControl id="firstName" isRequired>
            <FormLabel> Name </FormLabel>
            <Input 
                placeholder ="Enter your name" 
                onChange={(e)=>setName(e.target.value)} />   {/* e means event */}
        </FormControl>
        <FormControl id="email" isRequired>
            <FormLabel> Email </FormLabel>
            <Input 
                placeholder ="Enter your email" 
                onChange={(e)=>setEmail(e.target.value)} />   {/* e means event */}
        </FormControl>
        <FormControl id="firstName" isRequired>
            <FormLabel> Name </FormLabel>
            <Input 
                placeholder ="Enter your name" 
                onChange={(e)=>setPassword(e.target.value)} />   {/* e means event */}
        </FormControl>
        <FormControl id="password" isRequired>
            <FormLabel> Email </FormLabel>
            <InputGroup>
                <Input 
                type='password'
                placeholder ="Enter your password" 
                onChange={(e)=>setPassword(e.target.value)} />  
                <InputRightElement width="4.5rem">
                    {show? "Hide" : "Show"}
                </InputRightElement>
            </InputGroup>
            
        </FormControl>
    
</VStack>
  )
}

export default signup
